import { useState } from "react";
import "./App.css";
import pick from "./pick.png";
import rock from "./rock.png";
import { GiSpade, GiLaserBlast } from "react-icons/gi";
import ReactPlayer from "react-player";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import "./fonts/PrehistoricCaveman-PRJ7.ttf";
import { useLocation } from "wouter";
import rocket from "./rocket.png";

function Main() {
  const getItem = (item) => {
    localStorage.getItem(item);
  };

  const setItem = (item, value) => {
    localStorage.setItem(item, value);
  };

  if (!Number(getItem("points"))) {
    setItem("points", 1);
  }
  const keys = Object.keys(localStorage);

  const undefinedBool = keys.every(
    (curr) => localStorage.getItem(curr) === "undefined",
  );

  if (undefinedBool) {
    setItem("points", 0);
    setItem("addCoins", 1);
    setItem("hasRocket", false);
  }

  const coin = getItem("points");
  const addCoins = getItem("addCoins");
  const hasRocket = getItem("hasRocket");

  // const [coin, setCoin] = useState(getItem("points"));
  // const [addCoins, setAddCoins] = useState(getItem("addCoins"));

  const [open, setOpen] = useState(false);
  const [launch, setLaunch] = useState(false);
  // const [hasRocket, setHasRocket] = useState(getItem("hasRocket"));
  const [location, setLocation] = useLocation();

  const handleClickOpen = () => {
    setOpen(true);
  };

  const goBack = () => {
    setLocation("/mars");
  };

  const handleClose = () => {
    setOpen(false);
  };

  const kill = () => {
    setItem("addCoins", 1);
    setItem("points", 0);
    setItem("hasRocket", false);
    window.location.reload();
  };

  const killBoss = () => {
    setItem("hasRocket", true);
    setLaunch(false);
    window.location.href = "/boss-battle";
  };

  setItem("addCoins", addCoins);
  setItem("points", coin);
  setItem("hasRocket", hasRocket);
  return (
    <div className={"container"}>
      <div className={"header"}>
        <div className="coin-wrapper">
          <p className={"coin"}>|{coin}</p>
        </div>
      </div>
      <div className="body">
        <div className={"mine"}>
          <div className="coin-wrapper dph">
            <div>sph (skulls per hit) = {addCoins}</div>
          </div>
          <div className="miner">
            <img
              src={pick}
              className="Pick"
              onClick={() => {
                setItem("points", `${Number(coin) + Number(addCoins)}`);
              }}
            ></img>
            <div className="rock-wrapper">
              <img
                src={rock}
                className="Rock animate__animated animate__bounce"
                onClick={() => setItem("addCoins", addCoins + 1)}
              ></img>
              <img
                src={rock}
                className="Rock animate__animated animate__bounce"
                onClick={() => setItem("addCoins", addCoins + 1)}
              ></img>
            </div>
          </div>
          {getItem("hasRocket") ? (
            <img src={rocket} width={50} onClick={goBack}></img>
          ) : null}
        </div>
        <div className={"buy"}>
          <List
            component="nav"
            aria-label="secondary mailbox folders"
            className="list"
          >
            <ListItem
              button
              onClick={() => {
                if (coin < 20) {
                  handleClickOpen();
                  return;
                }
                setItem("points", coin - 20);
                setItem("addCoins", addCoins + 1);
              }}
            >
              <GiSpade></GiSpade> &nbsp; |20 shovel
            </ListItem>
            <ListItem
              button
              onClick={() => {
                if (coin < 40) {
                  handleClickOpen();
                  return;
                }
                setItem("points", coin - 40);
                setItem("addCoins", addCoins + 2);
              }}
            >
              üë∑‚Äç‚ôÇÔ∏è |40 miner helper
            </ListItem>
            <ListItem
              button
              onClick={() => {
                if (coin < 100) {
                  handleClickOpen();
                  return;
                }
                setItem("points", coin - 100);
                setItem("addCoins", addCoins + 10);
              }}
            >
              üöú |100 digger
            </ListItem>
            <ListItem
              button
              onClick={() => {
                if (coin < 1000) {
                  handleClickOpen();
                  return;
                }
                setItem("points", coin - 1000);
                setItem("addCoins", addCoins + 100);
              }}
            >
              <GiLaserBlast color="red"></GiLaserBlast>&nbsp; |1000 laser
            </ListItem>
            <ListItem
              button
              onClick={() => {
                if (coin < 10000) {
                  handleClickOpen();
                  return;
                }
                setItem("hasRocket", true);
                setItem("points", coin - 10000);
                setLaunch(true);
              }}
            >
              üöÄ |10,000 Rocket
            </ListItem>
            <ListItem
              button
              onClick={() => {
                handleClickOpen();
              }}
            >
              GIVE ALL COINS TO CHARITY
            </ListItem>
          </List>
        </div>
      </div>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Ya really wanna get bankrupt?"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            If you get bankrupt, your whole economy will be destroyed and NO
            REDOING.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Nah
          </Button>
          <Button onClick={kill} color="primary" autoFocus>
            Yeah
          </Button>
        </DialogActions>
      </Dialog>
      <Dialog
        open={launch}
        onClose={killBoss}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <ReactPlayer
          url="https://youtu.be/SHJUoiS9VsI"
          playing={true}
          width={"100%"}
        ></ReactPlayer>
      </Dialog>
    </div>
  );
}

export default Main;
