import { useState } from "react";
import "./App.css";
import pick from "./pick.png";
import rock from "./rock.png";

function App() {
  const [coin, setCoin] = useState(0);
  return (
    <div>
      <p className={"coins"}>{coin}</p>
      <img src={pick} className="Pick" onClick={() => setCoin(coin + 1)}></img>
      <img src={rock} className="Rock"></img>
    </div>
  );
}

export default App;
