import { useState, useEffect } from "react";
import "./App.css";
import pick from "./pick.png";
import rock from "./rock.png";
import { GiSpade, GiLaserBlast } from "react-icons/gi";
import ReactPlayer from "react-player";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import "./fonts/PrehistoricCaveman-PRJ7.ttf";
import { useLocation } from "wouter";
import rocket from "./rocket.png";

function Main() {
  const get = (item) => {
    localStorage.getItem(item);
  };
  const set = (item, value) => {
    localStorage.setItem(item, value);
  };

  const caseBool = !Object.values(localStorage).every(
    (curr) =>
      !isNaN(JSON.parse(localStorage[curr])) &&
      JSON.parse(localStorage[curr]) !== undefined,
  );
  const isItemFalsy = Object.values(localStorage).every(
    (curr) => curr === undefined || curr === NaN,
  );
  if (caseBool || isItemFalsy) {
    set("coin", 0);
    set("addCoin", 1);
    set("hasRocket", false);
  }

  // const [coin, setCoin] = useState(1);
  // const [addCoins, setAddCoins] = useState(get("addCoin"));

  const [open, setOpen] = useState(false);
  const [launch, setLaunch] = useState(false);
  // const [hasRocket, setHasRocket] = useState(get("hasRocket"));
  const [location, setLocation] = useLocation();

  console.log({
    coin: [get("coin")],
    addCoins: [get("addCoin")],
    hasRocket: [get("hasRocket")],
  });

  const handleClickOpen = () => {
    setOpen(true);
  };

  const goBack = () => {
    setLocation("/mars");
  };

  const handleClose = () => {
    setOpen(false);
  };

  const kill = () => {
    set("addCoin", 1);
    set("coin", 0);
    set("hasRocket", false);
    window.location.reload();
  };

  const killBoss = () => {
    set("hasRocket", true);
    setLaunch(false);
    window.location.href = "/boss-battle";
  };

  return (
    <div className={"container"}>
      <div className={"header"}>
        <div className="coin-wrapper">
          <p className={"coin"}>|{get("coin")}</p>
        </div>
      </div>
      <div className="body">
        <div className={"mine"}>
          <div className="coin-wrapper dph">
            <div>sph (skulls per hit) = {get("addCoin")}</div>
          </div>
          <div className="miner">
            <img
              src={pick}
              className="Pick"
              onClick={() => {
                set("coin", `${Number(get("coin")) + Number(get("addCoin"))}`);
              }}
            ></img>
            <div className="rock-wrapper">
              <img
                src={rock}
                className="Rock animate__animated animate__bounce"
                onClick={() => set("addCoin", get("addCoins") + 1)}
              ></img>
              <img
                src={rock}
                className="Rock animate__animated animate__bounce"
                onClick={() => set("addCoin", get("addCoins") + 1)}
              ></img>
            </div>
          </div>
          {get("hasRocket") ? (
            <img src={rocket} width={50} onClick={goBack}></img>
          ) : null}
        </div>
        <div className={"buy"}>
          <List
            component="nav"
            aria-label="secondary mailbox folders"
            className="list"
          >
            <ListItem
              button
              onClick={() => {
                if (get("coin") < 20) {
                  handleClickOpen();
                  return;
                }
                set("coin", get("coin") - 20);
                set("addCoin", get("addCoins") + 1);
              }}
            >
              <GiSpade></GiSpade> &nbsp; |20 shovel
            </ListItem>
            <ListItem
              button
              onClick={() => {
                if (get("coin") < 40) {
                  handleClickOpen();
                  return;
                }
                set("coin", get("coin") - 40);
                set("addCoin", get("addCoins") + 2);
              }}
            >
              👷‍♂️ |40 miner helper
            </ListItem>
            <ListItem
              button
              onClick={() => {
                if (get("coin") < 100) {
                  handleClickOpen();
                  return;
                }
                set("coin", get("coin") - 100);
                set("addCoin", get("addCoins") + 10);
              }}
            >
              🚜 |100 digger
            </ListItem>
            <ListItem
              button
              onClick={() => {
                if (get("coin") < 1000) {
                  handleClickOpen();
                  return;
                }
                set("coin", get("coin") - 1000);
                set("addCoin", get("addCoins") + 100);
              }}
            >
              <GiLaserBlast color="red"></GiLaserBlast>&nbsp; |1000 laser
            </ListItem>
            <ListItem
              button
              onClick={() => {
                if (get("coin") < 10000) {
                  handleClickOpen();
                  return;
                }
                set("hasRocket", true);
                set("coin", get("coin") - 10000);
                setLaunch(true);
              }}
            >
              🚀 |10,000 Rocket
            </ListItem>
            <ListItem
              button
              onClick={() => {
                handleClickOpen();
              }}
            >
              GIVE ALL COINS TO CHARITY
            </ListItem>
          </List>
        </div>
      </div>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Ya really wanna get bankrupt?"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            If you get bankrupt, your whole economy will be destroyed and NO
            REDOING.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Nah
          </Button>
          <Button onClick={kill} color="primary" autoFocus>
            Yeah
          </Button>
        </DialogActions>
      </Dialog>
      <Dialog
        open={launch}
        onClose={killBoss}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <ReactPlayer
          url="https://youtu.be/SHJUoiS9VsI"
          playing={true}
          width={"100%"}
        ></ReactPlayer>
      </Dialog>
    </div>
  );
}

export default Main;
