import { useState } from "react";
import "./App.css";
import pick from "./pick.png";
import rock from "./rock.png";

function App() {
  const [coin, setCoin] = useState(0);
  return (
    <div className={"container"}>
      <div className="header">
        <p className={"coins"}>{coin}</p>
      </div>
      <div style={{ flex: 1, justifyContent: "space-evenly" }}>
        <div>
          <img
            src={pick}
            className="Pick"
            onClick={() => setCoin(coin + 1)}
          ></img>
          <img src={rock} className="Rock"></img>
        </div>
        <div>
          <button>yee</button>
        </div>
      </div>
    </div>
  );
}

export default App;
