import { useState } from "react";
import "./App.css";
import pick from "./pick.png";
import rock from "./rock.png";

function App() {
  const getCookie = (cname) => {
    const name = cname + "=";
    const decodedCookie = decodeURIComponent(document.cookie);
    const ca = decodedCookie.split(";");
    for (let i = 0; i < ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) == " ") {
        c = c.substring(1);
      }
      if (c.indexOf(name) == 0) {
        return c.substring(name.length, c.length);
      }
    }
    return "";
  };
  const [coin, setCoin] = useState(getCookie("points"));
  const [addCoins, setAddCoins] = useState(1);
  document.cookie = `points=${coin}`;
  console.log(getCookie("points"));
  return (
    <div className={"container"}>
      <div className={"header"}>
        <div className="coin-wrapper">
          <p className={"coin"}>{coin}</p>
        </div>
      </div>
      <div className="body">
        <div className={"mine"}>
          <img
            src={pick}
            className="Pick"
            onClick={() => setCoin(coin + addCoins)}
          ></img>
          <img src={rock} className="Rock"></img>
        </div>
        <div className={"buy"}>
          <button
            onClick={() => {
              console.log("bro", addCoins);
              setAddCoins(`${parseInt(addCoins, 10) + 1}`);
              setCoin(coin - 2);
            }}
          >
            miner helper üë∑‚Äç‚ôÇÔ∏è
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
