import React, { useState } from "react";

const Boss = () => {
  const [bossShooting, setBossShooting] = useState(false);
  const [bossState, setBossState] = useState("./boss-deactive.png");

  if (!bossShooting) {
    setBossState("./boss-deactive.png");
  } else {
    setBossState("./boss-active.png");
  }

  return (
    <div className="boss-wrapper">
      <button onClick={setBossState(!bossShooting)}>Click me</button>
      <img src={bossState}></img>
    </div>
  );
};

export default Boss;
