import { useState } from "react";
import "./App.css";
import pick from "./pick.png";
import rock from "./rock.png";
import { GiSpade, GiLaserBlast } from "react-icons/gi";
import ReactPlayer from "react-player";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./fonts/PrehistoricCaveman-PRJ7.ttf";
import Boss from "./boss";

function App() {
  const getCookie = (cname) => {
    const name = cname + "=";
    const decodedCookie = decodeURIComponent(document.cookie);
    const ca = decodedCookie.split(";");
    for (let i = 0; i < ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) == " ") {
        c = c.substring(1);
      }
      if (c.indexOf(name) == 0) {
        return c.substring(name.length, c.length);
      }
    }
    return "";
  };
  if (!Number(getCookie("addCoins"))) {
    document.cookie = `addCoins=${Number(getCookie("addCoins")) + 1}`;
  }
  const [coin, setCoin] = useState(getCookie("points"));
  const [addCoins, setAddCoins] = useState(Number(getCookie("addCoins")));

  const [open, setOpen] = useState(false);
  const [launch, setLaunch] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    window.location.replace("");
  };

  const kill = () => {
    document.cookie = "points=0;addCoins=1";
    document.location.reload();
  };

  const killBoss = () => {
    setLaunch(false);
  };

  document.cookie = `points=${coin}; addCoins=${addCoins}`;

  return (
    <Router>
      <div className={"container"}>
        <div className={"header"}>
          <div className="coin-wrapper">
            <p className={"coin"}>{coin}</p>
          </div>
        </div>
        <div className="body">
          <div className={"mine"}>
            <img
              src={pick}
              className="Pick"
              onClick={() => setCoin(`${Number(coin) + Number(addCoins)}`)}
            ></img>
            <img
              src={rock}
              className="Rock animate__animated animate__bounce"
              onClick={() => setAddCoins(addCoins + 1)}
            ></img>
          </div>
          <div className={"buy"}>
            <List
              component="nav"
              aria-label="secondary mailbox folders"
              className="list"
            >
              <ListItem
                button
                onClick={() => {
                  if (coin < 20) {
                    handleClickOpen();
                    return;
                  }
                  setCoin(coin - 20);
                  setAddCoins(addCoins + 1);
                }}
              >
                <GiSpade></GiSpade> &nbsp; $20 shovel
              </ListItem>
              <ListItem
                button
                onClick={() => {
                  if (coin < 40) {
                    handleClickOpen();
                    return;
                  }
                  setCoin(coin - 40);
                  setAddCoins(addCoins + 2);
                }}
              >
                👷‍♂️ $40 miner helper
              </ListItem>
              <ListItem
                button
                onClick={() => {
                  if (coin < 100) {
                    handleClickOpen();
                    return;
                  }
                  setCoin(coin - 100);
                  setAddCoins(addCoins + 10);
                }}
              >
                🚜 $100 digger
              </ListItem>
              <ListItem
                button
                onClick={() => {
                  if (coin < 600) {
                    handleClickOpen();
                    return;
                  }
                  setCoin(coin - 1000);
                  setAddCoins(addCoins + 100);
                }}
              >
                <GiLaserBlast color="red"></GiLaserBlast>&nbsp; $1000 laser
              </ListItem>
              <ListItem
                button
                onClick={() => {
                  if (coin < 10000) {
                    handleClickOpen();
                    return;
                  }
                  setCoin(coin - 10000);
                  setLaunch(true);
                }}
              >
                🚀 $10,000 Rocket
              </ListItem>
              <ListItem
                button
                onClick={() => {
                  const confirmBool = window.confirm(
                    "Do you really want to do it?",
                  );
                  const curr = coin;
                  console.log("curr", curr);
                  if (confirmBool) {
                    document.cookie = `points=0;addCoins=-1`;
                    window.location.reload();
                  }
                }}
              >
                GIVE ALL COINS TO CHARITY
              </ListItem>
            </List>
          </div>
        </div>
        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">
            {"Ya really wanna get bankrupt?"}
          </DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              If you get bankrupt, your whole economy will be destroyed and NO
              REDOING.
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              Nah
            </Button>
            <Button onClick={kill} color="primary" autoFocus>
              Yeah
            </Button>
          </DialogActions>
        </Dialog>
        <Dialog
          open={launch}
          onClose={handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <ReactPlayer
            url="https://youtu.be/SHJUoiS9VsI"
            playing={true}
            width={"100%"}
          ></ReactPlayer>
        </Dialog>
        <Switch>
          <Route path="/boss-battle">
            <Boss></Boss>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
